import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Chart from './components/Chart';
import VolumeSlider from './components/RangeSlider';
import CSVReader from 'react-csv-reader';

class App extends Component {
	constructor(props){
		super(props);
		this.updateRange= this.updateRange.bind(this);
		this.handleForce= this.handleForce.bind(this);
		this.state = {
			sliderValue:5,
			chartData:{}
		}
	}
	componentWillMount(){
		this.getChartData();
	}
	updateRange(val) {
		const chartData = this.state.chartData;
		chartData.datasets = [
			  {
				label:'Population',
				data:[
				  val,
				  (val)*2,
				  (val)*3,
				  (val)*3,
				  (val)*4,
				  (val)*5
				],
				backgroundColor:[
				  'red'
				]
			  }
			]
		this.setState({
		  sliderValue: val,
		  chartData: chartData
		});
		console.log(this.state.sliderValue);
    }

	getChartData(){
    // Ajax calls here
		this.setState({
		  chartData:{
			labels: ['1 year','2 year','3 year','4 year','5 year'],
			datasets:[
			  {
				label:'Population',
				data:[
				  0,20,40,50,70
				],
				backgroundColor:[
				  'red'
				]
			  },
			  {
				label:'Population',
				data:[
				  10,40,60,70,90
				],
				backgroundColor:[
				  '#447DB9'
				]
			  },
			  {
				label:'Population',
				data:[
				  30,50,90,100,190
				],
				backgroundColor:[
				  '#65BFC7'
				]
			  }
			]
		  }
		});
	}
	handleForce(arr,name){
		const chartData = this.state.chartData;
		chartData.labels = arr[0];
		chartData.datasets = [{
				label:'population',
				data:arr[1],
				backgroundColor:[
				  'red'
				]
			}];
		this.setState({
		  chartData: chartData
		});
		console.log(this.state.sliderValue);
		console.log(arr[0]);	
	}
  render() {
	  var stylePadding={
		  width: 600
	  }
    return (
      <div className="App" style={stylePadding}>
        <div className="">
            <h2>Welcome to React</h2>
            <Chart chartData={this.state.chartData} location="1 year" legendPosition="bottom" redraw/>
		    <VolumeSlider range={this.state.sliderValue} action={this.updateRange}/>
			<CSVReader
				cssClass="csv-input"
				label="Select CSV with secret Death Star statistics"
				onFileLoaded={this.handleForce}
				onError={this.handleDarkSideForce}
				inputId="ObiWan"
				inputStyle={{color: 'red'}}
			/>
		</div>
      </div>
    );
  }
}

export default App;
