	constructor(props){
		super(props);
		this.state = {
		}
	}
	componentWillMount(){
	}
	
	render() {
	  var stylePadding={
		  width: 600
	  }
    return (
      <div className="App" style={stylePadding}>
        <div className="">
            <h2>Welcome to React</h2>
		</div>
      </div>
    );
  }
}

export default App;
