import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Chart from './components/Chart';
import VolumeSlider from './components/RangeSlider';

class App extends Component {
	constructor(props){
		super(props);
		this.updateRange= this.updateRange.bind(this);
		this.state = {
			sliderValue:5,
			chartData:{}
		}
	}
		componentWillMount(){
    this.getChartData();
}
	updateRange(val) {
		this.setState({
		  sliderValue: val
		});
		this.getChartData();
		//console.log();
    }

	getChartData(){
    // Ajax calls here
		console.log(this.state.chartData.datasets);
		this.setState(state=>{
			state.nested.data=[
				  this.state.sliderValue,
				  (this.state.sliderValue)*2,
				  (this.state.sliderValue)*3,
				  (this.state.sliderValue)*3,
				  (this.state.sliderValue)*4,
				  (this.state.sliderValue)*5
				]
				return state
		});
	}
  render() {
    return (
      <div className="App">
        <div className="">
           <h2>Welcome to React</h2>
          <Chart chartData={this.state.chartData} location="Massachusetts" legendPosition="bottom" redraw/>
		  <VolumeSlider range={this.state.sliderValue} action={this.updateRange}/>
        </div>
      </div>
    );
  }
}

export default App;
