import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Chart from './components/Chart';
import VolumeSlider from './components/RangeSlider';

class App extends Component {
	constructor(props){
		super(props);
		this.updateRange= this.updateRange.bind(this);
		this.state = {
			sliderValue:1,
			chartData:{},
			baseData:{}
		}
	}
	componentWillMount(){
		this.setBaseData();
		this.getChartData();
	}
	updateRange(val) {
		const chartData = this.state.chartData;
		var i;
		chartData.labels=this.state.chartData.labels;
		const newData = {};
		chartData.datasets = [
			  {
				label:'Population',
				data:[
				  val*10,
				  (val)*20+50,
				  (val)*30+100,
				  (val)*40+125,
				  (val)*45+175
				],
				backgroundColor:[
				  '#447DFF'
				]
			  },
			  {
				label:'Population1',
				data:[
				  val*chartData.datasets[1].data[0],
				  (val)*chartData.datasets[1].data[1],
				  (val)*chartData.datasets[1].data[2],
				  (val)*chartData.datasets[1].data[3],
				  (val)*chartData.datasets[1].data[4]
				],
				backgroundColor:[
				  '#447DB9'
				]
			  },
			  {
				label:'Population2',
				data:[
				  val*chartData.datasets[2].data[0],
				  (val)*chartData.datasets[2].data[1],
				  (val)*chartData.datasets[2].data[2],
				  (val)*chartData.datasets[2].data[3],
				  (val)*chartData.datasets[2].data[4]
				],
				backgroundColor:[
				  '#65BFC7'
				]
			  }
			];
		this.setState({
		  sliderValue: val,
		  chartData: chartData
		});
		console.log(this.state.chartData);
    }
	setBaseData(){
		this.setState({
			chartData:{
			labels: ['1 year','2 year','3 year','4 year','5 year'],
			datasets:[
			  {
				label:'Population',
				data:[
				  0,20,40,50,70
				],
				backgroundColor:[
				  '#447DFF'
				]
			  },
			  {
				label:'Population1',
				data:[
				  0,30,50,70,120
				],
				backgroundColor:[
				  '#447DB9'
				]
			  },
			  {
				label:'Population2',
				data:[
				  0,50,70,110,190
				],
				backgroundColor:[
				  '#65BFC7'
				]
			  }
			]
		  }
		});
		console.log(this.state.baseData);
	}
	datasetKeyProvider(){ return Math.random(); }
	getChartData(){
    // Ajax calls here
	}
  render() {
	  var stylePadding={
		  width: 600
	  }
    return (
      <div className="App" style={stylePadding}>
        <div className="">
            <h2>Welcome to React</h2>
            <Chart chartData={this.state.chartData} location="1 year" legendPosition="bottom" redraw datasetKeyProvider={this.datasetKeyProvider}/>
		    <VolumeSlider range={this.state.sliderValue} action={this.updateRange}/>
		</div>
      </div>
    );
  }
}

export default App;
