import React, {Component}  from 'react';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';
const style = { width: 400, margin: 50 };
const marks = {
  1:'$7k',
  2: '$19k',
  3: '$49k',
  4:'$79k',
  5:'$199k'
};
const API = 'https://sheets.googleapis.com/v4/spreadsheets/1JbLq-KRysRSD6Xptw24p3I79takWfFwCTSY7nYbakPI/values:batchGet?'+
'ranges=Investment!B3:B7&valueRenderOption=UNFORMATTED_VALUE&majorDimension=ROWS&key=AIzaSyBhw79t2J0aRVOT2a0P0-Jh81TvwhK0VMI';
class VolumeSlider extends Component {
 constructor(props) {
    super(props);
  }
  componentDidMount(){
		this.setDatsets();
	}
 setDatsets(){
     fetch(API).then(response => response.json()).then(data => {
      let baseInvestments = data.valueRanges[0].values;
	  marks[1]='$'+baseInvestments[0]*.001+'k';
	  marks[2]='$'+baseInvestments[1]*.001+'kkk';
	  marks[3]='$'+baseInvestments[2]*.001+'k';
	  marks[4]='$'+baseInvestments[3]*.001+'k';
	  marks[5]='$'+baseInvestments[4]*.001+'k';	
	});//marks={1:this.state.marks.val1,2:this.state.marks.val2,3:this.state.marks.val3,4:this.state.marks.val4,5:this.state.marks.val5}
 }
  handleOnChange = (value) => {
	 this.props.handle(value)
  }
 
  render() {
    return (
      
    <div style={style}>
      <Slider dots min={1} max={5} marks={marks} step={1} onChange={this.handleOnChange} defaultValue={1} />
    </div>
    )
  }
}
export default VolumeSlider;
