import React, { Component } from 'react';
import './App.css';
import Chart from './components/Chart';
import VolumeSlider from './components/RangeSlider';
import RCSlider from './components/RCSlider';
import dataa from './testFile/sample.xlsx';
const WorstSets=[[
	[267.38,2087.29,5618.92,10859.78,17807.09]],
	[[1069.50,8349.18,22475.67,43439.10,71228.35]],
	[[2848.13,22234.23,59853.69,115680.21,189684.20]],
	[[4746.67,37055.35,99751.57,192791.51,316125.84]],
	[[13301.93,103842.92,279541.15,540273.81,885902.64]]
];
const BestSets=[
	[[7001.78,47418.45,123354.20,234806.56,381772.76]],
	[[28007.12,189673.81,493416.81,939226.26,1527091.02]],
	[[74584.18,505109.60,1313990.43,2501200.36,4066709.79]],
	[[124301.26,841810.73,2189883.61,4168476.08,6777538.81]],
	[[348338.55,2359068.01,6136871.63,11681626.57,18993194.60]]
];
const MidSets=[
	[[1564.65,10944.41,28657.61,54701.80,89074.19]],
	[[6258.62,43777.62,114630.44,218807.19,356296.77]],
	[[16666.98,116581.71,305265.84,582693.07,948833.79]],
	[[27777.02,194293.94,508753.07,971110.58,1581317.13]],
	[[77841.59,544484.18,1425716.08,2721414.47,4431441.10]]
];
class App extends Component {
	constructor(props){
		super(props);
		this.updateRange= this.updateRange.bind(this);
		this.updateScenario=this.updateScenario.bind(this);
		this.state = {
			sliderValue:1,
			chartData:{
				labels: ['1 year','2 year','3 year','4 year','5 year'],
				datasets:[{
				label:'Population1',
				data:[],
				backgroundColor:[
				  '#447DB9'
				]
				}]
			},
			ScenarioValue:1
		}
	}
	componentWillMount(){
		this.setBaseData();
	}
	updateScenario(val){

		this.setState({
		  ScenarioValue: val
		});
		this.updateRange(this.state.sliderValue);
	}
	updateRange(val) {
		//console.log(val);
		const chartData = this.state.chartData;
		const baseData = this.state.baseData;
		const sv=this.state.ScenarioValue;
		switch(sv){
			case 1:
				chartData.datasets[0].data=WorstSets[val-1];
			case 2:
				chartData.datasets[0].data=MidSets[val-1];
			case 3:
				chartData.datasets[0].data=BestSets[val-1];
		}
		this.setState({
		  sliderValue: val,
		  chartData: chartData
		});
    }
	setBaseData(){
		this.updateRange(1);
	}
	datasetKeyProvider(){ return Math.random(); }
  render() {
	  var stylePadding={
		  width: 600
	  }
    return (
      <div className="App" style={stylePadding}>
        <div className="">
            <h2>Welcome to React</h2>
            <Chart chartData={this.state.chartData} location="1 year" legendPosition="bottom" redraw datasetKeyProvider={this.datasetKeyProvider}/>
		    <VolumeSlider range={this.state.sliderValue} handle={this.updateRange}/>
			<RCSlider range={this.state.ScenarioValue} handle={this.updateScenario}/>
		</div>
      </div>
    );
  }
}

export default App;
