import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Chart from './components/Chart';
import VolumeSlider from './components/RangeSlider';

class App extends Component {
	constructor(props){
		super(props);
		this.updateRange= this.updateRange.bind(this);
		this.state = {
			sliderValue:5,
			chartData:{},
			baseData:{}
		}
	}
	componentWillMount(){
		this.setBaseData();
		this.getChartData();
	}
	updateRange(val) {
		const chartData = this.state.chartData;
		chartData.datasets = [
			  {
				label:'Population',
				data:[
				  val,
				  (val)*2,
				  (val)*3,
				  (val)*3,
				  (val)*4,
				  (val)*5
				],
				backgroundColor:[
				  'red'
				]
			  }
			]
		this.setState({
		  sliderValue: val,
		  chartData: chartData
		});
		console.log(this.state.sliderValue);
    }
	setBaseData(){
		this.setState({
		  baseData:{
			labels: ['1 year','2 year','3 year','4 year','5 year'],
			datasets:[
			  {
				label:'Population',
				data:[
				  0,20,40,50,70
				],
				backgroundColor:[
				  '#447DFF'
				]
			  },
			  {
				label:'Population',
				data:[
				  0,30,50,70,120
				],
				backgroundColor:[
				  '#447DB9'
				]
			  },
			  {
				label:'Population',
				data:[
				  0,50,70,110,190
				],
				backgroundColor:[
				  '#65BFC7'
				]
			  }
			]
		  }
		});
	}
	getChartData(){
    // Ajax calls here
		console.log(this.state.baseData);
		this.setState({
		  chartData:{
			labels: this.state.baseData.labels,
			datasets:this.state.baseData.datasets
		  }
		});
	}
  render() {
	  var stylePadding={
		  width: 600
	  }
    return (
      <div className="App" style={stylePadding}>
        <div className="">
            <h2>Welcome to React</h2>
            <Chart chartData={this.state.chartData} location="1 year" legendPosition="bottom" redraw/>
		    <VolumeSlider range={this.state.sliderValue} action={this.updateRange}/>
		</div>
      </div>
    );
  }
}

export default App;
