import React, {Component} from 'react';
import {Bar,Line,Pie} from 'react-chartjs-2';
class Chart extends Component{
	constructor(){
		super(props);
		this.state={
			chartData:{
				labels:['x','y','z','m'],
				datasets:[
				{
					label:'Population',
					data:[
					10,20,30,40
					]
				}
				]
			}
		}
	}
	render (){ 
	return(
	<div className="chart">
		<Bar
		data={this.state.chartData}
		options={{maintainAspectRatio:false}}
		/>
	</div>
	)}
}
export default Chart;
